import React from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";

const ProfileItem = (profile) => {
  console.log(profile);
  console.log(profile.profile.user);
  return (
    <div className="profile bg-light">
      <div>
        <h2>{profile.profile.user.name}</h2>
        <p>
          {profile.profile.status}{" "}
          {profile.profile.company && (
            <span> at {profile.profile.company}</span>
          )}
        </p>
        <p className="my-1">
          {profile.profile.location && <span>{profile.profile.location}</span>}
        </p>
        <Link
          to={`/profile/${profile.profile.user._id}`}
          className="btn btn-primary"
        >
          View Profile
        </Link>
      </div>
      <ul>
        {profile.profile.skills.slice(0, 4).map((skill, index) => (
          <li key={index} className="text-primary">
            <i className="fas fa-check" /> {skill}
          </li>
        ))}
      </ul>
    </div>
  );
};

ProfileItem.propTypes = {
  profile: PropTypes.object.isRequired,
};

export default ProfileItem;

